import styled from "styled-components";

export const Container = styled.div`
  border-radius: 8px;
  box-shadow: var(--shadow);
  padding: 1.5rem 1rem 0;
`;
export const Header = styled.div`
  margin-bottom: 1.5rem;
  span {
    font-size: 3rem;
    font-weight: 900;
    margin: 0;
  }
`;
export const Icon = styled.img`
  height: 3em;
  margin-left: 1rem;
`;
